<?php	echo '<h2>Witaj!</h2><hr class="style-one"></hr>';	if(!empty($user)) {		if($user['group_id'] == 3) {			if($option == 1) {				$db->query('UPDATE users SET accepted = 1 WHERE id = '.(int)$_GET['id']);				alert(1, 'Konto dydaktyka zostało aktywowane.');			} else if ($option == 12) {				$db->query('DELETE FROM users WHERE id = '.(int) $_GET['id']);				header('Location: '.$config['page_url']);			}			$promoters_to_accept = $db->select_multi('SELECT * FROM users WHERE group_id = 2 AND accepted = 0');			if($promoters_to_accept) {			echo '<h3>Dydaktycy do akceptacji</h3><table class="table table-bordered table-hover"><thead><tr>						<th>Imie</th>						<th>Nazwisko</th>						<th>Wydział</th>						<th width="137px"></th>					  </tr></thead><tbody>';				if($promoters_to_accept) {					foreach($promoters_to_accept as $s) {						$dep = $db->select_single('SELECT * FROM departments WHERE id = '.$s['subject_ids']);						echo '<tr><td>'.$s['name'].'</td><td>'.$s['lastname'].'</td><td>'.$dep['name'].'</td><td><a href="'.$config['page_url'].'?option=1&id='.$s['id'].'"><input type="submit" class="btn btn-info btn-xs" value="Akceptuj"></a> <a href="'.$config['page_url'].'?option=12&id='.$s['id'].'"><input type="submit" class="btn btn-danger btn-xs" value="Odrzuc"></a></td></tr>';					}				}				echo '</tbody></table><br/>';			}		} elseif($user['group_id'] == 2) {			if($user['accepted'] == 0) {				alert(2, 'Poczekaj na akceptację Twojego konta!');				exit();			}			if($option == 1) {				$db->query('UPDATE users SET accepted = 1 WHERE id = '.(int)$_GET['id']);				alert(1, 'Potwierdziłeś studenta.');			} elseif($option == 2) {				$db->query('UPDATE theses_topics SET accepted = 1 WHERE id = '.(int)$_GET['id']);				alert(1, 'Zaakceptowałeś temat pracy.');			} else if ($option == 3) {				$db->query('UPDATE users SET promoter_id = 0 WHERE id = '.(int) $_GET['id']);				alert(1, 'Odrzuciłes studenta.');			}//end if			$students_to_accept = $db->select_multi('SELECT * FROM users WHERE group_id = 1 AND accepted = 0 AND promoter_id = '.$user['id']);			if($students_to_accept) {			echo '<h3>Studenci do akceptacji</h3><table class="table table-bordered table-hover"><thead><tr>						<th>Imie</th>						<th>Nazwisko</th>						<th>Wydziały</th>						<th>Kierunki</th>						<th width="137px"></th>					  </tr></thead><tbody>';				if($students_to_accept) {					foreach($students_to_accept as $s) {						echo '<tr><td>'.$s['name'].'</td><td>'.$s['lastname'].'</td><td>-</td><td>-</td><td><a href="'.$config['page_url'].'?option=1&id='.$s['id'].'"><input type="submit" class="btn btn-info btn-xs" value="Akceptuj"></a> <a href="'.$config['page_url'].'?option=3&id='.$s['id'].'"><input type="submit" class="btn btn-danger btn-xs" value="Odrzuć"></a></td></tr>';					}				}				echo '</tbody></table><br/>';			}			$threads = $db->select_multi('SELECT * FROM theses_topics WHERE accepted = 0 AND promoter_id = '.$user['id']);			if($threads) {			echo '<h3>Tematy do akceptacji</h3><table class="table table-bordered table-hover"><thead><tr>						<th>Temat</th>						<th>Opis</th>						<th>Student</th>						<th>Data Wysłania</th>						<th width="57px"></th>					  </tr></thead><tbody>';				if($threads) {					foreach($threads as $t) {						echo '<tr><td>'.$t['topic'].'</td><td>'.$t['description'].'</td><td>-</td><td>-</td><td><a href="'.$config['page_url'].'?option=2&id='.$t['id'].'"><input type="submit" class="btn btn-info btn-xs" value="Akceptuj"></a></td></tr>';					}				}				echo '</tbody></table><br/>';			}		} elseif($user['group_id'] == 1) {			if($user['promoter_id'] == 0) {				alert(2, 'Nie wybrałeś jeszcze promotora!');			}		}	}?>