id DESC');			if($edits) {				foreach($edits as $e) {					echo '<tr><td>'.date('H:i, d.m.Y', $e['edit_date']).'</td><td>'.$e['text'].'</td><td>'.$e['comment'].'</td><td><a href="'.$config['page_url'].'uploads/'.$e['these_id'].'-'.$e['edit_date'].'.'.$e['file_type'].'"><input type="submit" class="btn btn-primary btn-xs" value="Pobierz Załącznik"/></a></td></tr>';				}			}?>							</tbody>		</table>		<a href="<?php echo $config['page_url'];?>?page=mytopic&option=1"><input type="submit" class="btn btn-primary btn-ls" value="Dodaj Wersję"/></a><?php		}	} else {		if($option == 1) {			if($user['promoter_id'] > 0) {				$var = $db->select_single('SELECT * FROM theses_topics WHERE id = '.(int)$_GET['id']);				if($var['student_id'] == 0) {					$db->query('UPDATE theses_topics SET student_id = '.$user['id'].' WHERE id = '.(int)$_GET['id']);					header('Location: '.$config['page_url'].'?page=mytopic');				}			}		} elseif($option == 5) {			if(!empty($_POST['send'])) {				if(!empty($_POST['name']) && !empty($_POST['desc'])) {					$db->query("INSERT INTO `theses_topics` (`topic`, `promoter_id`, `student_id`, `add_date`, `take_date`, `description`, `by_student`, `accepted`) VALUES ('".$_POST['name']."', '".$user['promoter_id']."', '0', '".$time_now."', '0', '".$_POST['desc']."', '".$user['id']."', '0');");					alert(1, 'Temat został wysłany do promotora.');				} else {					alert(2, 'Uzupełnij wszystkie dane!');				}			}?>		<form action="" method="POST">			<input type="hidden" name="send" value="1"/>			<div class="row">				<div class="col-lg-12">					<div class="form-group">						<label>Nazwa Tematu:</label>						<input class="form-control" name="name" value=""/>					</div>					<div class="form-group">						<label>Opis Tematu:</label>						<textarea class="form-control" name="desc" rows="6" style="resize:none;"></textarea>					</div>					<input type="submit" class="btn btn-primary btn-ls" value="Zaproponuj Temat"/>				</div>			</div>		</form><?php		} else {			alert(2, 'Nie wybrałeś jeszcze tematu pracy!');			$topics = $db->select_multi('SELECT t.*, u.name as p_name, u.lastname as p_lastname FROM theses_topics t JOIN users u ON u.id = t.promoter_id LEFT JOIN users u2 ON u2.id = t.student_id WHERE t.student_id = 0 AND t.accepted = 1 AND t.promoter_id = '.$user['promoter_id']);			echo '<table class="table table-bordered table-hover">			<thead><tr>				<th>Temat Pracy</th>				<th>Opis</th>				<th>Promotor</th>				<th width="57px"></th>			  </tr></thead><tbody>';			if($topics) {				foreach($topics as $t) {					echo '<tr><td>'.$t['topic'].'</td><td>'.$t['description'].'</td><td>'.$t['p_name'].' '.$t['p_lastname'].'</td><td>					<a href="'.$config['page_url'].'?page=mytopic&option=1&id='.$t['id'].'"><input type="submit" class="btn btn-info btn-xs" value="Wybierz"></a></td></tr>';				}			}			echo '</tbody></table><a href="'.$config['page_url'].'?page=mytopic&option=5"><input type="submit" class="btn btn-primary" value="Zaproponuj temat"/></a>';		}	}?>